import React from "react";

export default function Layout({ children, setRoute }) {
    const links = {
        0: ["pizzasList", "All Pizzas"],
        1: ["order", "Order"],
        2: ["home", "Home"],
    };

    return (
        <div className="flex flex-col justify-between">
            <div className="flex justify-around cursor-pointer">
                {links.map((link, index) => (
                    <p
                        className={
                            route === link[index][0] ? "text-blue-800" : null
                        }
                        onClick={() => setRoute(link[index][0])}
                    >
                        {link[index][1]}
                    </p>
                ))}
                <p onClick={() => setRoute("pizzasList")}>All pizzas</p>
                <p onClick={() => setRoute("order")}>Order</p>
                <p onClick={() => setRoute("home")}>Home</p>
            </div>
            {children}
        </div>
    );
}
