import { pizzasDB } from "../DB";
import { useState } from "react";
import CardComponent from "./cardItems/cardComponent";
import BasiqButton from "./basiqButton";
import Picture from "./cardItems/picture";
import Description from "./cardItems/description";
import Name from "./cardItems/name";

export default function PizzaList({ addPizza, selectedPizza, order }) {
    const [pizzas, setPizzas] = useState(pizzasDB);
    return (
        <div>
            {pizzas.map((pizza, i) => (
                <CardComponent key={pizza.name}>
                    <div className="flex flex-col justify-between items-center">
                        <div>
                            <Name name={pizza.name} />
                            <Description description={pizza.description} />
                        </div>
                        <BasiqButton
                            text={"add"}
                            // setState={addPizza((prevState) => {
                            //     return [...prevState, selectedPizza];
                            // })}
                            item={selectedPizza}
                        />
                    </div>
                    <Picture picture={pizza.img} />
                </CardComponent>
            ))}
        </div>
    );
}
