import { pizzasDB } from "./DB";
import { useState } from "react";

function App() {
    const [pizzas, setPizzas] = useState(pizzasDB);

    return (
        <div className="App">
            {pizzas.map((pizza) => {
                return <img src={pizza.img} alt={`${pizza.name}-image`} />;
            })}
        </div>
    );
}

export default App;
