import { pizzasDB } from "../DB";
import { useState } from "react";
import CardComponent from "./pizzaCard/cardComponent";
import BasiqButton from "./basiqButton";
import Picture from "./pizzaCard/picture";
import Description from "./pizzaCard/description";
import Name from "./pizzaCard/name";

export default function PizzaList() {
    const [pizzas, setPizzas] = useState(pizzasDB);
    return (
        <div className="App">
            {pizzas.map((pizza) => (
                <CardComponent>
                    <div className="flex flex-col justify-between">
                        <div>
                            <Name name={pizza.name} />
                            <Description description={pizza.description} />
                        </div>
                        <BasiqButton text={"add"} />
                    </div>
                    <Picture picture={pizza.img} />
                </CardComponent>
            ))}
        </div>
    );
}
