import { pizzasDB } from "./DB";
import { useState } from "react";
import CardComponent from "./components/pizzaCard/cardComponent";
import BasiqButton from "./components/basiqButton";
import Picture from "./components/pizzaCard/picture";
import Description from "./components/pizzaCard/description";
import Name from "./components/pizzaCard/name";

function App() {
    const [pizzas, setPizzas] = useState(pizzasDB);
    return (
        <div className="App">
            <CardComponent>
                <div className="flex flex-col justify-between items-center">
                    <div>
                        <Name name={pizzas[0].name} />
                        <Description description={pizzas[0].description} />
                    </div>
                    <BasiqButton text={"add"} />
                </div>
                <Picture picture={pizzas[0].img} />
            </CardComponent>
        </div>
    );
}

export default App;
