import { pizzasDB } from "./DB";
import { useState } from "react";
import PizzaCard from "./components/pizzaCard";
import BasiqButton from "./components/basiqButton";
import PizzaPicture from "./components/pizzaPicture";
import PizzaDescription from "./components/pizzaDescription";
function App() {
    const [pizzas, setPizzas] = useState(pizzasDB);
    // console.log(pizzas);
    return (
        <div className="App">
            <PizzaCard>
                <PizzaDescription description={pizzas[0].description} />
                <BasiqButton text={"add"} />
                <PizzaPicture picture={pizzas[0].img} />
            </PizzaCard>
        </div>
    );
}

export default App;
